import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './index.css';

const App = () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [showAll, setShowAll] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    axios
      .get('http://localhost:3001/notes')
      .then((response) => {
        setNotes(response.data);
      })
      .catch((error) => {
        console.log('Error fetching notes:', error);
      });
  }, []);

  const addNote = (event) => {
    event.preventDefault();

    const noteObject = {
      content: newNote,
      important: Math.random() > 0.5,
    };

    axios
      .post('http://localhost:3001/notes', noteObject)
      .then((response) => {
        setNotes(notes.concat(response.data));
        setNewNote('');
        setSuccessMessage('Note added successfully');
        setTimeout(() => {
          setSuccessMessage('');
        }, 3000);
      })
      .catch((error) => {
        console.log('Error adding note:', error);
        setErrorMessage('Failed to add note');
        setTimeout(() => {
          setErrorMessage('');
        }, 3000);
      });
  };

  const toggleImportance = (id) => {
    const note = notes.find((note) => note.id === id);
    const changedNote = { ...note, important: !note.important };

    axios
      .put(`http://localhost:3001/notes/${id}`, changedNote)
      .then((response) => {
        setNotes(notes.map((note) => (note.id !== id ? note : response.data)));
        setSuccessMessage('Note importance toggled successfully');
        setTimeout(() => {
          setSuccessMessage('');
        }, 3000);
      })
      .catch((error) => {
        console.log('Error toggling note importance:', error);
        setErrorMessage('Failed to toggle note importance');
        setTimeout(() => {
          setErrorMessage('');
        }, 3000);
      });
  };

  const handleNoteChange = (event) => {
    setNewNote(event.target.value);
  };

  const notesToShow = showAll ? notes : notes.filter((note) => note.important);

  return (
    <div>
      <h1>Notes</h1>
      <div className="error">{errorMessage}</div>
      <div className="success">{successMessage}</div>
      <button onClick={() => setShowAll(!showAll)}>
        Show {showAll ? 'important' : 'all'}
      </button>
      <ul>
        {notesToShow.map((note) => (
          <li key={note.id}>
            {note.content}
            <button onClick={() => toggleImportance(note.id)}>
              {note.important ? 'make not important' : 'make important'}
            </button>
          </li>
        ))}
      </ul>
      <form onSubmit={addNote}>
        <input value={newNote} onChange={handleNoteChange} />
        <button type="submit">Add Note</button>
      </form>
    </div>
  );
};

export default App;
